mapper.select:
SELECT u.*
FROM users u
         JOIN chat_participants cp ON u.user_id = cp.user_id
WHERE cp.room_id = #{roomId}
  AND u.user_id != #{excludeUserId}

SELECT last_read_time
FROM chat_read
WHERE room_id = #{roomId}
  AND user_id = #{userId}

SELECT crm.room_id FROM chat_room_members crm WHERE crm.user_id IN
#{userId}
GROUP BY crm.room_id HAVING COUNT(*) = #{size} AND COUNT(*) = ( SELECT COUNT(*) FROM chat_room_members WHERE room_id = crm.room_id ) LIMIT 1
